import { useState, useEffect } from "react";
import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Dimensions,
  Alert,
  Pressable,
  Button,
} from "react-native";

export default function App() {
  const [attendances, setattendances] = useState({
    philipp: -1,
    ida: -1,
    lena: -1,
    marie: -1,
    georg: -1,
    karin: -1,
    claudi: -1,
  });

  const [user_texts, setuser_texts] = useState({
    philipp: "",
    ida: "",
    lena: "",
    marie: "",
    georg: "",
    karin: "",
    claudi: "",
  });

  const [att_num, setatt_num] = useState(0);
  const [non_att_num, setnon_att_num] = useState(0);
  const [no_feedback, setno_feedback] = useState(0);
  const [reload, set_reaload] = useState(false);

  function update_attendances(key, value) {
    setattendances((prevState) => ({
      ...prevState,
      [key]: value,
    }));
  }

  const get_data = async () => {
    console.log("fetch data");
    fetch("http://localhost:5002/current_users")
      .then((response) => response.json())
      .then((json) => {
        let att_num = 0;
        let non_att_num = 0;
        let no_feedback = 0;
        json.data.forEach((elm) => {
          update_attendances(elm.name, elm.attendances);
          if (elm.attendance == 1) {
            att_num++;
          } else if (elm.attendance == 0) {
            non_att_num++;
          } else {
            no_feedback++;
          }
          setatt_num(att_num);
          setnon_att_num(non_att_num);
          setno_feedback(no_feedback);
        });
        set_reload(!reload)
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    get_data();
  }, []);

  useEffect(() => {
    get_data();
  }, []);

  useEffect(() => {}, [reload]);

  function bool_to_color(b) {
    if (b == 1) {
      return "green";
    } else if (b == 0) {
      return "red";
    } else {
      return "white";
    }
  }

  return (
    <View style={styles.main_container}>
      <View style={styles.user_attendance}>
        <Button
          onPress={() => {
            console.log("pressed"), get_data();
            console.log(attendances.philipp);
          }}
        >
          <Text>Click me</Text>
        </Button>
        <View style={styles.user_attendance_left}>
          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}></Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.philipp) },
              ]}
            ></View>
          </View>

          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.marie) },
              ]}
            ></View>
          </View>

          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.georg) },
              ]}
            ></View>
          </View>

          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.karin) },
              ]}
            ></View>
          </View>
        </View>
        <View style={styles.user_attendance_right}>
          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.ida) },
              ]}
            ></View>
          </View>
          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.lena) },
              ]}
            ></View>
          </View>
          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[
                styles.attendance_icon,
                { backgroundColor: bool_to_color(attendances.claudi) },
              ]}
            ></View>
          </View>
          <View style={styles.user_component}>
            <Image
              style={styles.user_picture}
              source={require("./assets/face.png")}
            ></Image>

            <Text style={styles.user_text}>ich komme heute später</Text>
            <View
              style={[styles.attendance_icon, { backgroundColor: "white" }]}
            ></View>
          </View>
        </View>
      </View>
      <View style={styles.attendance_eval}>
        <Text style={styles.attendance_eval_text}>
          {att_num} Leute kommen heute
        </Text>
        <Text style={styles.attendance_eval_text}>
          {non_att_num} Leute kommen heute nicht
        </Text>
        <Text style={styles.attendance_eval_text}>
          Von {no_feedback} Leuten gibt es noch keine Rückmeldung
        </Text>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  main_container: {
    flex: 1,
    borderWidth: 5,
    borderColor: "black",
    flexDirection: "column",
    backgroundColor: "#fff",
    justifyContent: "flex-start",
    alignItems: "stretch",
    margin: "2%",
  },
  user_attendance: {
    flex: 4,
    flexDirection: "row",
    borderWidth: 5,
    borderColor: "#f00000",
    backgroundColor: "#fff",
    justifyContent: "space-around",
  },
  user_attendance_left: {
    flex: 1,
    borderWidth: 5,
    textAlign: "center", // <-- the magic
    alignContent: "space-between",
    justifyContent: "space-between",
    borderColor: "blue",
  },
  user_attendance_right: {
    flex: 1,
    borderWidth: 5,
    alignContent: "space-between",
    justifyContent: "space-between",
    textAlign: "center", // <-- the magic
    borderColor: "blue",
  },
  user_component: {
    margin: 10,
    marginHorizontal: 30,
    flexDirection: "row",
    borderRadius: 40,
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    borderWidth: 5,
    backgroundColor: "#707070",
    borderColor: "purple",
  },
  user_picture: {
    borderRadius:
      Math.round(
        Dimensions.get("window").width + Dimensions.get("window").height
      ) / 2,
    borderWidth: 1,
    // borderColor: "black",
    // padding: 10,
    width: (Dimensions.get("window").width * 0.5) / 10,
    height: (Dimensions.get("window").width * 0.5) / 10,
    marginLeft: 10,
  },
  user_text: {
    flex: 5,
  },
  attendance_icon: {
    borderWidth: 5,
    borderColor: "black",
    marginBottom: -95,
    marginRight: -25,
    borderRadius:
      Math.round(
        Dimensions.get("window").width + Dimensions.get("window").height
      ) / 2,
    width: (Dimensions.get("window").width * 0.5) / 14,
    height: (Dimensions.get("window").width * 0.5) / 14,
  },
  attendance_eval: {
    flex: 1,
    flexDirection: "row",
    borderWidth: 5,
    borderColor: "black",
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  attendance_eval_text: {
    flex: 1,
    borderWidth: 5,
    borderColor: "green",
    textAlign: "center", // <-- the magic
    fontWeight: "bold",
    fontSize: 18,
  },
});
